-- ----------------------------
-- Table structure for shelves
-- ----------------------------
DROP TABLE IF EXISTS `shelves`;
CREATE TABLE `shelves` (
  `id` varchar(36) NOT NULL,
  `shelvesId` varchar(36) NOT NULL,
  `createTime` bigint(20) NOT NULL,
  `memberId` varchar(36) NOT NULL,
  `clotheId` varchar(36) NOT NULL,
  `image` varchar(255) DEFAULT NULL,
  `price` decimal(10,0) DEFAULT NULL,
  `count` varchar(255) DEFAULT '1',
  `colour` varchar(255) DEFAULT NULL,
  `size` varchar(255) DEFAULT 'L',
  `province` varchar(255) DEFAULT '江苏',
  `city` varchar(255) DEFAULT '苏州',
  `address` varchar(255) DEFAULT '科灵路',
  `linkman` varchar(255) DEFAULT NULL,
  `phone` varchar(20) DEFAULT NULL,
  `cate` varchar(255) DEFAULT NULL,
  `state` int(11) NOT NULL COMMENT '0:取消，1:已下单，2:已配送，3:已收货，4:确认收货',
  `name` varchar(255) DEFAULT NULL,
  `clothenumber` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

